import React from "react";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import HomeIcon from "@material-ui/icons/Home";
import AddIcon from "@material-ui/icons/AddBoxRounded";
import Button from "@material-ui/core/Button";
import auth from "../service/auth-helper";
import { Link, useLocation, useNavigate } from "react-router-dom";
import styled from "styled-components";

const AddBookButton = styled.button`
  background: #fff;
  display: flex;
  cursor: pointer;

  span {
    align-self: center;
  }
`;

const DonateButton = styled.button`
  padding: 8px;
  border-radius: 8px;
  background: var(--primary-color);
  color: #ececec;
`;

const RightNavBar = styled.div`
  position: absolute;
  right: 10px;

  a {
    display: inline-block;
  }
`;

const HomeButtonWrapper = styled.div`
  display: flex;
  justify-content: center;
`;

const DonateButttonWrapper = styled.div`
  align-self: center;
`;

const isActive = (location, path) => {
  if (location.pathname === path) return { color: "#69f0ae" };
  else return { color: "#ffffff" };
};

const Menu = (props) => {
  const location = useLocation();
  const navigate = useNavigate();
  const doRedirectToPaymentPage = () => {
    navigate("/payment");
  };
  return (
    <AppBar position="static">
      <Toolbar>
        <Typography variant="h6" color="inherit">
          Bookie
        </Typography>
        <HomeButtonWrapper>
          <Link to="/">
            <IconButton aria-label="Home" style={isActive(location, "/")}>
              <HomeIcon />
            </IconButton>
          </Link>
          <DonateButttonWrapper>
            <DonateButton onClick={doRedirectToPaymentPage}>
              Donate me
            </DonateButton>
          </DonateButttonWrapper>
        </HomeButtonWrapper>
        <RightNavBar>
          {auth.isAuthenticated() && (
            <Link to="/add-book">
              <AddBookButton>
                <AddIcon />
                <span>Add Book</span>
              </AddBookButton>
            </Link>
          )}
          <span style={{ float: "right" }}>
            {!auth.isAuthenticated() && (
              <span>
                <Link to="/signup">
                  <Button style={isActive(location, "/signup")}>Sign up</Button>
                </Link>
                <Link to="/login">
                  <Button style={isActive(location, "/login")}>Sign In</Button>
                </Link>
              </span>
            )}
            {auth.isAuthenticated() && (
              <span>
                <Button
                  color="inherit"
                  onClick={() => {
                    auth.clearJWT(() => navigate("/"));
                  }}
                >
                  Sign out
                </Button>
              </span>
            )}
          </span>
        </RightNavBar>
      </Toolbar>
    </AppBar>
  );
};

export default Menu;
